      default = true;
      description = "Enable Yoga touchscreen support";
    };

    enableRotation = mkOption {
      type = types.bool;
      default = true;
      description = "Enable screen rotation support for Yoga convertibles";
    };

    enableStylus = mkOption {
      type = types.bool;
      default = true;
