      type = types.bool;
      default = true;
      description = "Enable Yoga-specific function keys";
    };

    enableCamera = mkOption {
      type = types.bool;
      default = true;
      description = "Enable camera support";
    };

    enableAudio = mkOption {
      type = types.bool;
      default = true;
      description = "Enable Yoga-specific audio configuration";
    };
