{
  description = "pantherOS - Declarative NixOS Configuration";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";

    disko = {
      url = "github:nix-community/disko";
      inputs.nixpkgs.follows = "nixpkgs";
    };

    nixos-hardware = {
      url = "github:NixOS/nixos-hardware";
      inputs.nixpkgs.follows = "nixpkgs";
    };

    home-manager = {
      url = "github:nix-community/home-manager";
      inputs.nixpkgs.follows = "nixpkgs";
    };
  };

  outputs = { self, nixpkgs, disko, nixos-hardware, home-manager, ... }: {
    nixosConfigurations = {
      # Yoga - Lenovo Yoga 7 2-in-1 (battery-optimized, lightweight development)
      yoga = nixpkgs.lib.nixosSystem {
        system = "x86_64-linux";
        modules = [
          ./hosts/yoga
          disko.nixosModules.disko
          home-manager.nixosModules.home-manager
          {
            home-manager.useGlobalPkgs = true;
            home-manager.useUserPackages = true;
            home-manager.users.hbohlen = ./home/hbohlen/default.nix;
          }
        ];
      };

      # Zephyrus - ASUS ROG Zephyrus M16 (performance workstation, heavy development workflows)
      zephyrus = nixpkgs.lib.nixosSystem {
        system = "x86_64-linux";
        modules = [
          ./hosts/zephyrus
          disko.nixosModules.disko
          home-manager.nixosModules.home-manager
          {
            home-manager.useGlobalPkgs = true;
            home-manager.useUserPackages = true;
            home-manager.users.hbohlen = ./home/hbohlen/default.nix;
          }
        ];
      };

      # Hetzner VPS - Hetzner Cloud VPS (primary development server)
      hetzner-vps = nixpkgs.lib.nixosSystem {
        system = "x86_64-linux";
        modules = [
          ./hosts/servers/hetzner-vps
          disko.nixosModules.disko
          home-manager.nixosModules.home-manager
          {
            home-manager.useGlobalPkgs = true;
            home-manager.useUserPackages = true;
            home-manager.users.hbohlen = ./home/hbohlen/default.nix;
          }
        ];
      };

      # OVH Cloud VPS - OVH Cloud VPS (secondary server)
      ovh-vps = nixpkgs.lib.nixosSystem {
        system = "x86_64-linux";
        modules = [
          ./hosts/servers/ovh-cloud
          disko.nixosModules.disko
          home-manager.nixosModules.home-manager
          {
            home-manager.useGlobalPkgs = true;
            home-manager.useUserPackages = true;
            home-manager.users.hbohlen = ./home/hbohlen/default.nix;
          }
        ];
      };
    };

    # Development shell for working on the configuration
    devShells.x86_64-linux.default = with nixpkgs.legacyPackages.x86_64-linux; mkShell {
      buildInputs = [
        nix
        git
        home-manager
        disko.packages.x86_64-linux.disko
        nixos-rebuild
        nixos-generators
        nil
        nixfmt
      ];

      shellHook = ''
        echo "pantherOS Development Environment"
        echo "================================="
        echo ""
        echo "Available tools:"
        echo "- nix, nixos-rebuild, git"
        echo "- home-manager, disko"
        echo "- nil (Nix language server)"
        echo "- nixfmt (formatter)"
        echo ""
        echo "To test a configuration: nixos-rebuild build --flake .#<hostname>"
        echo "To enter a Nix shell in this directory: nix develop"
      '';
    };
  };
}

{ config, lib, pkgs, ... }:

{
  # Hardware configuration for Hetzner Cloud VPS
  # Based on common Hetzner VPS specifications and virtualized environment

  # Use generic x86_64 configuration for cloud environment
  nixpkgs.hostPlatform = lib.mkDefault "x86_64-linux";

  # Enable virtualization support (KVM/VMware modules work in cloud environments)
  boot.initrd.availableKernelModules = [
    "ata_piix" "uhci_hcd" "xen_blkfront" "vmw_pvscsi" "virtio_pci"
    "virtio_scsi" "virtio_blk" "sr_mod" "virtio_balloon" "virtio_console"
  ];

  boot.kernelModules = [
    "kvm-intel" "kvm-amd"  # Enable KVM for nested virtualization support
  ];

  # Enable necessary hardware drivers for cloud environment
  hardware.enableRedistributableFirmware = true;

  # Enable CPU frequency scaling for performance optimization
  powerManagement.cpuFreqGovernor = lib.mkDefault "performance";

  # File system configuration is handled by disko.nix, not here
  # to prevent conflicts with the Btrfs impermanence setup
  fileSystems = {
    "/".device = "/dev/disk/by-label/nixos";
    "/".fsType = "btrfs";
  };

  # Enable swap using the partition defined in disko.nix
  swapDevices = [
    { device = "/dev/disk/by-label/swap"; randomEncryption = true; }
  ];

  # Configure for virtualized environment
  services.qemuGuest.enable = true;

  # Enable hardware-specific services that are appropriate for cloud
  services.udev.enable = true;

  # Virtualization support for containers (needed for Podman/Docker)
  virtualisation = {
    # Podman configured in main host configuration
    # Enable support for virtualization tools as needed
    libvirtd.enable = false; # Disable full libvirt for security unless needed
  };

  # Network interface configuration (usually handled by cloud init)
  # but we'll specify basic requirements
  networking = {
    useDHCP = true; # Use DHCP for IP assignment
  };

  # Disable laptop-specific services
  services.tlp = null; # Disable TLP which is for laptops
  powerManagement.powertop.enable = false;
}

{
  disko.devices = {
    disk = {
      "sda" = {
        type = "disk";
        content = {
          type = "gpt";
          partitions = {
            # BIOS boot partition for compatibility
            bios-boot = {
              size = "1M";
              type = "EF02";
              priority = 1;
            };

            # ESP (1GB) - Single kernel, server focused
            esp = {
              size = "1G";
              type = "EF00";
              content = {
                type = "filesystem";
                format = "vfat";
                mountpoint = "/boot";
                mountOptions = [
                  "umask=0077"
                  "fmask=0077"
                  "dmask=0077"
                ];
              };
              priority = 2;
            };

            # Swap (32GB) - Matches RAM for hibernation capability
            swap = {
              size = "32G";
              type = "8200";
              content = {
                type = "swap";
                randomEncryption = true;
              };
              priority = 3;
            };

            # Btrfs root with impermanence
            root = {
              size = "max";
              type = "8300";
              content = {
                type = "btrfs";
                extraArgs = [ "-f" ];
                btrfsConfig = {
                  label = "hetzner-nixos";
                  features = [ "block-group-tree" ];
                };

                subvolumes = [
                  # ===== PERMANENT SUBVOLUMES =====
                  {
                    name = "nix";
                    mountpoint = "/nix";
                    compress = "zstd:1";
                    neededForBoot = true;
                  }

                  {
                    name = "persist";
                    mountpoint = "/persist";
                    compress = "zstd:3";
                    neededForBoot = true;
                  }

                  {
                    name = "log";
                    mountpoint = "/var/log";
                    compress = "zstd:3";
                    neededForBoot = true;
                  }

                  {
                    name = "services";
                    mountpoint = "/var/lib/services";
                    compress = "zstd:2";
                    neededForBoot = true;
                  }

                  {
                    name = "caddy";
                    mountpoint = "/var/lib/caddy";
                    compress = "zstd:3";
                    neededForBoot = true;
                  }

                  {
                    name = "backup";
                    mountpoint = "/var/backup";
                    compress = "zstd:3";
                  }

                  {
                    name = "containers";
                    mountpoint = "/var/lib/containers";
                    compress = "zstd:1";
                    mountOptions = [ "nodatacow" ];
                  }

                  # ===== EPHEMERAL SUBVOLUMES =====
                  {
                    name = "root";
                    mountpoint = "/";
                    compress = "zstd:3";
                  }

                  {
                    name = "cache";
                    mountpoint = "/var/cache";
                    compress = "zstd:3";
                  }

                  {
                    name = "tmp";
                    mountpoint = "/var/tmp";
                    compress = "zstd:1";
                    mountOptions = [ "nodatacow" ];
                  }

                  # ===== ARCHIVE SUBVOLUMES =====
                  {
                    name = "snapshots";
                    mountpoint = "/.snapshots";
                    compress = "zstd:3";
                  }

                  {
                    name = "old_roots";
                    mountpoint = "/btrfs_tmp/old_roots";
                    compress = "zstd:3";
                  }
                ];
              };
              priority = 4;
            };
          };
        };
      };
    };
  };
}

{
  imports = [
    ./disko.nix
    ./hardware.nix
    ../../modules/shared/filesystems/server-btrfs.nix
  ];

  # Add server impermanence configuration
  pantherOS.serverImpermanence = {
    enable = true;
    performanceMode = "io-optimized";  # Optimized for container/database workloads
    snapshotPolicy = {
      frequency = "6h";      # Every 6 hours
      retention = "30d";     # 30-day retention
      scope = "critical";     # Critical subvolumes only
    };
  };

  # Basic server configuration will be expanded in later tasks
  system.stateVersion = "24.05";
}
