{ config, lib, ... }:

{
  imports = [
    ./server-impermanence.nix
    ./server-snapshots.nix
  ];

  config = {
    # Universal Btrfs mount options for servers
    fileSystems = {
      "/" = {
        options = [
          "compress=zstd:3"
          "noatime"
          "ssd"
          "space_cache=v2"
          "discard=async"
          "subvol=root"
        ];
      };

      "/nix" = {
        options = [
          "compress=zstd:1"
          "noatime"
          "ssd"
          "space_cache=v2"
          "discard=async"
          "subvol=nix"
        ];
      };

      "/persist" = {
        options = [
          "compress=zstd:3"
          "noatime"
          "ssd"
          "space_cache=v2"
          "discard=async"
          "subvol=persist"
        ];
      };

      "/var/log" = {
        options = [
          "compress=zstd:3"
          "noatime"
          "ssd"
          "space_cache=v2"
          "discard=async"
          "subvol=log"
        ];
      };

      "/var/lib/containers" = {
        options = [
          "compress=zstd:1"
          "noatime"
          "nodatacow"
          "nodatasum"
          "ssd"
          "space_cache=v2"
          "discard=async"
          "subvol=containers"
        ];
      };
    };

    # Enable Btrfs quota support for multi-tenant isolation
    services.btrfs.autoScrub = {
      enable = true;
      interval = "weekly";
      fileSystems = [ "/" ];
    };
  };
}

{ config, lib, pkgs, ... }:

with lib;

let
  cfg = config.pantherOS.security.systemdHardening;
in
{
  options.pantherOS.security.systemdHardening = {
    enable = mkEnableOption "PantherOS systemd service hardening";

    enableSystemdAttackSurfaceReduction = mkOption {
      type = types.bool;
      default = true;
      description = "Reduce systemd attack surface by disabling unused features";
    };

    enableUnifiedCgroupHierarchy = mkOption {
      type = types.bool;
      default = true;
      description = "Enable unified cgroup hierarchy for better isolation";
    };

    enableUserSlices = mkOption {
      type = types.bool;
      default = true;
      description = "Enable user slices for user session isolation";
    };

    defaultServiceMode = mkOption {
      type = types.enum [ "strict" "balanced" "relaxed" ];
      default = "strict";
      description = "Default security mode for services";
    };

    enableServiceSandboxing = mkOption {
      type = types.bool;
      default = true;
      description = "Enable service sandboxing by default";
    };

    globalServiceSettings = {
      enableAmbientCapabilities = mkOption {
        type = types.bool;
        default = false;
        description = "Whether to enable ambient capabilities by default";
      };

      enableSecureBits = mkOption {
        type = types.bool;
        default = true;
        description = "Whether to enable secure bits by default";
      };

      enableNoNewPrivileges = mkOption {
        type = types.bool;
        default = true;
        description = "Whether to enable no-new-privileges by default";
      };

      enablePrivateUsers = mkOption {
        type = types.bool;
        default = false;  # This can break some services
        description = "Whether to enable private users by default";
      };
    };
  };

  config = mkIf cfg.enable {
    # Systemd hardening configurations
    systemd = {
      # Systemd service hardening settings
      enableHardening = true;

      # Reduce attack surface
      service = mkIf cfg.enableSystemdAttackSurfaceReduction {
        # Default secure settings for all services
        default = {
          # Security settings
          NoNewPrivileges = mkDefault cfg.globalServiceSettings.enableNoNewPrivileges;

          # Isolation settings
          PrivateTmp = mkDefault true;
          PrivateDevices = mkDefault true;
          PrivateUsers = mkDefault cfg.globalServiceSettings.enablePrivateUsers;
          ProtectSystem = mkDefault "strict";
          ProtectHome = mkDefault true;
          ProtectHostname = mkDefault true;
          ProtectClock = mkDefault true;
          ProtectKernelTunables = mkDefault true;
          ProtectKernelModules = mkDefault true;
          ProtectControlGroups = mkDefault true;
          RestrictAddressFamilies = mkDefault [ "AF_INET" "AF_INET6" ];
          RestrictRealtime = mkDefault true;
          RestrictSUIDSGID = mkDefault true;
          RemoveIPC = mkDefault true;

          # Capabilities hardening
          AmbientCapabilities = mkIf cfg.globalServiceSettings.enableAmbientCapabilities [ ];
          NoExecPaths = mkDefault [
            "/tmp" "/var/tmp" "/dev/shm"
          ];
          ExecPaths = mkDefault [
            "/nix/store/*" "/run/current-system/sw/bin"
          ];
          ReadOnlyPaths = mkDefault [
            "/etc" "/usr" "/opt"
          ];
          ReadWritePaths = mkDefault [
            "/var" "/tmp" "/run" "/dev"
          ];
        };
      };

      # User session hardening
      user = mkIf cfg.enableUserSlices {
        enable = cfg.enableUserSlices;
        defaultSlice = mkIf (cfg.defaultServiceMode == "strict") "system.slice"; # More restrictive for user services
      };
    };

    # Kernel parameters for systemd hardening
    boot = mkIf cfg.enableUnifiedCgroupHierarchy {
      kernel.sysctl = {
        # Cgroup settings
        "kernel.unprivileged_userns_clone" = mkDefault 0; # Disable unprivileged user namespaces
        "kernel.ctrl-alt-del" = mkDefault 0; # Disable Ctrl+Alt+Del
        "kernel.modules_disabled" = mkDefault 0; # Can be set to 1 in very secure environments (disables module loading after boot)
        "kernel.kexec_load_disabled" = mkDefault 1; # Disable kexec (only when needed)
        "kernel.kptr_restrict" = mkDefault 2; # Restrict kernel pointer leaks
        "kernel.printk" = mkDefault [ 3 3 3 3 ]; # Restrict kernel messages
      };
    };

    # Extra packages for security monitoring
    environment.systemPackages = mkIf cfg.enableSystemdAttackSurfaceReduction [
      pkgs.systemd
      pkgs.acl  # For systemd ACLs
    ];

    # Additional security settings
    security = mkIf cfg.enableSystemdAttackSurfaceReduction {
      # Ensure systemd settings are properly applied
      protectKernelModules = true;
      protectKernelTunables = true;
    };
  };
}

{ config, lib, ... }:

with lib;

let
  cfg = config.pantherOS.security.firewall;
in
{
  options.pantherOS.security.firewall = {
    enable = mkEnableOption "PantherOS advanced firewall configuration";

    enableImplicitNetwork = mkOption {
      type = types.bool;
      default = true;
      description = "Whether to allow traffic on the local loopback and private networks by default";
    };

    allowPing = mkOption {
      type = types.bool;
      default = false;
      description = "Whether to allow incoming ping requests";
    };

    trustTailscale = mkOption {
      type = types.bool;
      default = false;
      description = "Whether to trust Tailscale interfaces completely";
    };

    trustedInterfaces = mkOption {
      type = types.listOf types.str;
      default = [ ];
      description = "List of trusted network interfaces that have more permissions";
    };

    allowedTCPPorts = mkOption {
      type = types.listOf types.port;
      default = [ ];
      description = "List of TCP ports to allow through the firewall";
    };

    allowedUDPPorts = mkOption {
      type = types.listOf types.port;
      default = [ ];
      description = "List of UDP ports to allow through the firewall";
    };

    allowedTCPPortRanges = mkOption {
      type = types.listOf (types.submodule {
        options = {
          from = mkOption { type = types.port; };
          to = mkOption { type = types.port; };
        };
      });
      default = [ ];
      description = "List of TCP port ranges to allow through the firewall";
    };

    allowedUDPPortRanges = mkOption {
      type = types.listOf (types.submodule {
        options = {
          from = mkOption { type = types.port; };
          to = mkOption { type = types.port; };
        };
      });
      default = [ ];
      description = "List of UDP port ranges to allow through the firewall";
    };

    extraForwardRules = mkOption {
      type = types.lines;
      default = "";
      description = "Additional iptables rules for the forward chain";
    };

    logRefusedConnections = mkOption {
      type = types.bool;
      default = false;
      description = "Whether to log refused connections";
    };
  };

  config = mkIf cfg.enable {
    # Advanced firewall configuration
    networking.firewall = {
      enable = true;
      enableIPv6 = true;  # Enable IPv6 firewall as well
      trustedInterfaces =
        cfg.trustedInterfaces
        ++ (mkIf cfg.trustTailscale [ "tailscale0" ]);

      # Apply implicit network settings
      enableImplicitNetwork = cfg.enableImplicitNetwork;

      # Allow ping if configured
      allowPing = cfg.allowPing;

      # Additional ports
      allowedTCPPorts = cfg.allowedTCPPorts;
      allowedUDPPorts = cfg.allowedUDPPorts;

      # Port ranges
      allowedTCPPortRanges = cfg.allowedTCPPortRanges;
      allowedUDPPortRanges = cfg.allowedUDPPortRanges;

      # Log refused connections if configured
      logRefusedConnections = cfg.logRefusedConnections;

      # Additional forward rules
      extraFilterForward = cfg.extraForwardRules;

      # Additional security settings
      synFloodProtector = "systemd";
      stateFile = "/var/lib/nixos/state-firewall";
    };

    # Additional security hardening for the firewall
    boot = {
      kernel.sysctl = {
        # Protect against SYN flood attacks
        "net.ipv4.tcp_syncookies" = mkDefault 1;
        "net.ipv4.tcp_synack_retries" = mkDefault 5;
        "net.ipv4.tcp_max_syn_backlog" = mkDefault 4096;

        # IP spoofing protection
        "net.ipv4.conf.all.rp_filter" = mkDefault 1;
        "net.ipv4.conf.default.rp_filter" = mkDefault 1;

        # Ignore ICMP broadcasts to prevent broadcast attacks
        "net.ipv4.icmp_echo_ignore_broadcasts" = mkDefault 1;

        # Prevent against man in the middle attacks
        "net.ipv4.conf.all.accept_redirects" = mkDefault 0;
        "net.ipv4.conf.all.secure_redirects" = mkDefault 0;
        "net.ipv4.conf.all.send_redirects" = mkDefault 0;

        # Accepting source route can be used for attacks
        "net.ipv4.conf.all.accept_source_route" = mkDefault 0;
        "net.ipv6.conf.all.accept_ra" = mkDefault 0;
        "net.ipv6.conf.all.accept_redirects" = mkDefault 0;
      };
    };
  };
}
