messages.content:
{
  description = "pantherOS - Declarative NixOS Configuration";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";

    disko = {
      url = "github:nix-community/disko";
      inputs.nixpkgs.follows = "nixpkgs";
    };

    nixos-hardware = {
      url = "github:NixOS/nixos-hardware";
      inputs.nixpkgs.follows = "nixpkgs";
    };

    home-manager = {
      url = "github:nix-community/home-manager";
      inputs.nixpkgs.follows = "nixpkgs";
    };
  };

  outputs = { self, nixpkgs, disko, nixos-hardware, home-manager, ... }: {
    nixosConfigurations = {
      # Yoga - Lenovo Yoga 7 2-in-1 (battery-optimized, lightweight development)
      yoga = nixpkgs.lib.nixosSystem {
        system = "x86_64-linux";
        modules = [
          ./hosts/yoga
          disko.nixosModules.disko
          home-manager.nixosModules.home-manager
          {
            home-manager.useGlobalPkgs = true;
            home-manager.useUserPackages = true;
            home-manager.users.hbohlen = ./home/hbohlen/default.nix;
          }
        ];
      };

      # Zephyrus - ASUS ROG Zephyrus M16 (performance workstation, heavy development workflows)
      zephyrus = nixpkgs.lib.nixosSystem {
        system = "x86_64-linux";
        modules = [
          ./hosts/zephyrus
          disko.nixosModules.disko
          home-manager.nixosModules.home-manager
          {
            home-manager.useGlobalPkgs = true;
            home-manager.useUserPackages = true;
            home-manager.users.hbohlen = ./home/hbohlen/default.nix;
          }
        ];
      };

      # Hetzner VPS - Hetzner Cloud VPS (primary development server)
      hetzner-vps = nixpkgs.lib.nixosSystem {
        system = "x86_64-linux";
        modules = [
          ./hosts/servers/hetzner-vps
          disko.nixosModules.disko
          home-manager.nixosModules.home-manager
          {
            home-manager.useGlobalPkgs = true;
            home-manager.useUserPackages = true;
            home-manager.users.hbohlen = ./home/hbohlen/default.nix;
          }
        ];
      };

      # OVH Cloud VPS - OVH Cloud VPS (secondary server)
      ovh-vps = nixpkgs.lib.nixosSystem {
        system = "x86_64-linux";
        modules = [
          ./hosts/servers/ovh-cloud
          disko.nixosModules.disko
          home-manager.nixosModules.home-manager
          {
            home-manager.useGlobalPkgs = true;
            home-manager.useUserPackages = true;
            home-manager.users.hbohlen = ./home/hbohlen/default.nix;
          }
        ];
      };
    };

    # Development shell for working on the configuration
    devShells.x86_64-linux.default = with nixpkgs.legacyPackages.x86_64-linux; mkShell {
      buildInputs = [
        nix
        git
        home-manager
        disko.packages.x86_64-linux.disko
        nixos-rebuild
        nixos-generators
        nil
        nixfmt
      ];

      shellHook = ''
        echo "pantherOS Development Environment"
        echo "================================="
        echo ""
        echo "Available tools:"
        echo "- nix, nixos-rebuild, git"
        echo "- home-manager, disko"
        echo "- nil (Nix language server)"
        echo "- nixfmt (formatter)"
        echo ""
        echo "To test a configuration: nixos-rebuild build --flake .#<hostname>"
        echo "To enter a Nix shell in this directory: nix develop"
      '';
    };
  };
}

{ config, lib, pkgs, ... }:

{
  # Hardware configuration for Hetzner Cloud VPS
  # Based on common Hetzner VPS specifications and virtualized environment

  # Use generic x86_64 configuration for cloud environment
  nixpkgs.hostPlatform = lib.mkDefault "x86_64-linux";

  # Enable virtualization support (KVM/VMware modules work in cloud environments)
  boot.initrd.availableKernelModules = [
    "ata_piix" "uhci_hcd" "xen_blkfront" "vmw_pvscsi" "virtio_pci"
    "virtio_scsi" "virtio_blk" "sr_mod" "virtio_balloon" "virtio_console"
  ];

  boot.kernelModules = [
    "kvm-intel" "kvm-amd"  # Enable KVM for nested virtualization support
  ];

  # Enable necessary hardware drivers for cloud environment
  hardware.enableRedistributableFirmware = true;

  # Enable CPU frequency scaling for performance optimization
  powerManagement.cpuFreqGovernor = lib.mkDefault "performance";

  # File system configuration is handled by disko.nix, not here
  # to prevent conflicts with the Btrfs impermanence setup
  fileSystems = {
    "/".device = "/dev/disk/by-label/nixos";
    "/".fsType = "btrfs";
  };

  # Enable swap using the partition defined in disko.nix
  swapDevices = [
    { device = "/dev/disk/by-label/swap"; randomEncryption = true; }
  ];

  # Configure for virtualized environment
  services.qemuGuest.enable = true;

  # Enable hardware-specific services that are appropriate for cloud
  services.udev.enable = true;

  # Virtualization support for containers (needed for Podman/Docker)
  virtualisation = {
    # Podman configured in main host configuration
    # Enable support for virtualization tools as needed
    libvirtd.enable = false; # Disable full libvirt for security unless needed
  };

  # Network interface configuration (usually handled by cloud init)
  # but we'll specify basic requirements
  networking = {
    useDHCP = true; # Use DHCP for IP assignment
  };

  # Disable laptop-specific services
  services.tlp = null; # Disable TLP which is for laptops
  powerManagement.powertop.enable = false;
}

{
  disko.devices = {
    disk = {
      "sda" = {
        type = "disk";
        content = {
          type = "gpt";
          partitions = {
            # BIOS boot partition for compatibility
            bios-boot = {
              size = "1M";
              type = "EF02";
              priority = 1;
            };

            # ESP (1GB) - Single kernel, server focused
            esp = {
              size = "1G";
              type = "EF00";
              content = {
                type = "filesystem";
                format = "vfat";
                mountpoint = "/boot";
                mountOptions = [
                  "umask=0077"
                  "fmask=0077"
                  "dmask=0077"
                ];
              };
              priority = 2;
            };

            # Swap (32GB) - Matches RAM for hibernation capability
            swap = {
              size = "32G";
              type = "8200";
              content = {
                type = "swap";
                randomEncryption = true;
              };
              priority = 3;
            };

            # Btrfs root with impermanence
            root = {
              size = "max";
              type = "8300";
              content = {
                type = "btrfs";
                extraArgs = [ "-f" ];
                btrfsConfig = {
                  label = "hetzner-nixos";
                  features = [ "block-group-tree" ];
                };

                subvolumes = [
                  # ===== PERMANENT SUBVOLUMES =====
                  {
                    name = "nix";
                    mountpoint = "/nix";
                    compress = "zstd:1";
                    neededForBoot = true;
                  }

                  {
                    name = "persist";
                    mountpoint = "/persist";
                    compress = "zstd:3";
                    neededForBoot = true;
                  }

                  {
                    name = "log";
                    mountpoint = "/var/log";
                    compress = "zstd:3";
                    neededForBoot = true;
                  }

                  {
                    name = "services";
                    mountpoint = "/var/lib/services";
                    compress = "zstd:2";
                    neededForBoot = true;
                  }

                  {
                    name = "caddy";
                    mountpoint = "/var/lib/caddy";
                    compress = "zstd:3";
                    neededForBoot = true;
                  }

                  {
                    name = "backup";
                    mountpoint = "/var/backup";
                    compress = "zstd:3";
                  }

                  {
                    name = "containers";
                    mountpoint = "/var/lib/containers";
                    compress = "zstd:1";
                    mountOptions = [ "nodatacow" ];
                  }

                  # ===== EPHEMERAL SUBVOLUMES =====
                  {
                    name = "root";
                    mountpoint = "/";
                    compress = "zstd:3";
                  }

                  {
                    name = "cache";
                    mountpoint = "/var/cache";
                    compress = "zstd:3";
                  }

                  {
                    name = "tmp";
                    mountpoint = "/var/tmp";
                    compress = "zstd:1";
                    mountOptions = [ "nodatacow" ];
                  }

                  # ===== ARCHIVE SUBVOLUMES =====
                  {
                    name = "snapshots";
                    mountpoint = "/.snapshots";
                    compress = "zstd:3";
                  }

                  {
                    name = "old_roots";
                    mountpoint = "/btrfs_tmp/old_roots";
                    compress = "zstd:3";
                  }
                ];
              };
              priority = 4;
            };
          };
        };
      };
    };
  };
}

{
  imports = [
    ./disko.nix
    ./hardware.nix
    ../../modules/shared/filesystems/server-btrfs.nix
  ];

  # Add server impermanence configuration
  pantherOS.serverImpermanence = {
    enable = true;
    performanceMode = "io-optimized";  # Optimized for container/database workloads
    snapshotPolicy = {
      frequency = "6h";      # Every 6 hours
      retention = "30d";     # 30-day retention
      scope = "critical";     # Critical subvolumes only
    };
  };

  # Basic server configuration will be expanded in later tasks
  system.stateVersion = "24.05";
}

{ config, lib, pkgs, ... }:

with lib;

let
  cfg = config.pantherOS.filesystems.impermanence;
in
{
  options.pantherOS.filesystems.impermanence = {
    enable = mkEnableOption "PantherOS impermanence configuration";

    enableBtrfsImpermanence = mkOption {
      type = types.bool;
      default = false;
      description = "Enable Btrfs-based impermanence using subvolumes and snapshots";
    };

    persistentPaths = mkOption {
      type = types.listOf types.str;
      default = [ "/persist" "/nix" "/var/log" ];
      description = "List of paths that should persist across reboots";
    };

    ephemeralPaths = mkOption {
      type = types.listOf types.str;
      default = [ "/tmp" "/var/tmp" "/run" ];
      description = "List of paths that are reset on each boot";
    };

    useTmpfsForEphemeral = mkOption {
      type = types.bool;
      default = true;
      description = "Use tmpfs for ephemeral paths (faster but uses RAM)";
    };

    backupOnBoot = mkOption {
      type = types.bool;
      default = false;
      description = "Create a backup of ephemeral paths before reset";
    };

    enableAutomaticSnapshots = mkOption {
      type = types.bool;
      default = false;
      description = "Enable automatic Btrfs snapshots before impermanence reset";
    };
  };

  config = mkIf cfg.enable {
    # Impermanence configuration
    environment.systemPackages = mkIf cfg.enableBtrfsImpermanence [
      pkgs.btrfs-progs
    ];

    # If using Btrfs impermanence
    systemd = mkIf cfg.enableBtrfsImpermanence {
      # Service to handle impermanence on boot
      services = {
        "btrfs-impermanence-setup" = mkIf cfg.enableBtrfsImpermanence {
          enable = true;
          description = "Btrfs-based Impermanence Setup";
          wantedBy = [ "multi-user.target" ];
          before = [ "network.target" ];
          serviceConfig = {
            Type = "oneshot";
            RemainAfterExit = true;
            User = "root";
          };

          script = let
            persistentPathsList = concatStringsSep " " cfg.persistentPaths;
            backupCmd = if cfg.backupOnBoot then
              "btrfs subvolume snapshot / /var/lib/impermanence/backup-$(date +%Y%m%d-%H%M%S) || true"
            else
              "# Backup disabled";
          in ''
            #!/bin/sh
            set -e

            echo "Starting Btrfs impermanence setup"

            # Create backup if enabled
            ${backupCmd}

            # Only reset root subvolume if we're using Btrfs
            if [ -f /sys/fs/btrfs/features/mixed_backrefs ]; then
              echo "Btrfs detected, setting up impermanence"

              # Create subvolume for current root if not exists
              if ! btrfs subvolume list / | grep -q "root"; then
                btrfs subvolume create /root-tmp
              fi

              # Create persistent directories
              ${concatStringsSep "\n  " (map (path: ''
                mkdir -p ${path}
                if ! mountpoint -q ${path}; then
                  echo "Making ${path} persistent"
                fi
              '') cfg.persistentPaths)}

              # Setup ephemeral paths
              ${concatStringsSep "\n  " (map (path: ''
                if [ ! -d ${path} ]; then
                  mkdir -p ${path}
                fi
              '') cfg.ephemeralPaths)}

              echo "Btrfs impermanence setup completed"
            else
              echo "Btrfs not detected, skipping impermanence setup"
            fi
          '';
        };
      };
    };

    # Mount configuration for impermanence
    fileSystems = mkIf cfg.useTmpfsForEphemeral (
      listToAttrs (map (path: {
        name = path;
        value = {
          fsType = "tmpfs";
          options = [ "size=100M" "mode=1777" "nr_inodes=10k" ];
        };
      }) cfg.ephemeralPaths)
    );

    # Additional impermanence-related packages
    environment.systemPackages = with pkgs; [
      coreutils
      util-linux
    ] ++ mkIf cfg.enableBtrfsImpermanence [ btrfs-progs ];

    # If using automatic snapshots
    services = mkIf (cfg.enableBtrfsImpermanence && cfg.enableAutomaticSnapshots) {
      # Timer for creating periodic snapshots
      cron = mkIf cfg.enableAutomaticSnapshots {
        enable = true;
        # This is a simplified approach - in practice we'd use systemd timers
        systemCrontab = ''
          # Create snapshots periodically
          # This is a placeholder - real implementation would use systemd timers
        '';
      };
    };

    # Systemd timers and services for snapshot management
    systemd = mkIf (cfg.enableBtrfsImpermanence && cfg.enableAutomaticSnapshots) {
      timers."btrfs-snapshot" = {
        enable = true;
        wantedBy = [ "timers.target" ];
        timerConfig = {
          OnCalendar = "daily";
          Persistent = true;
        };
      };

      services."btrfs-snapshot" = {
        enable = true;
        description = "Create Btrfs snapshots for impermanence";
        wantedBy = [ "multi-user.target" ];
        serviceConfig = {
          Type = "oneshot";
          User = "root";
        };

        script = ''
          #!/bin/sh
          set -e

          # Create timestamped snapshot
          TIMESTAMP=$(date +%Y%m%d-%H%M%S)
          SNAPSHOT_NAME="root-snapshot-$TIMESTAMP"

          echo "Creating Btrfs snapshot: $SNAPSHOT_NAME"
          btrfs subvolume snapshot / "/.snapshots/$SNAPSHOT_NAME"

          # Cleanup old snapshots (keep last 7)
          cd /.snapshots
          ls -1dt */ | tail -n +8 | xargs -r btrfs subvolume delete

          echo "Snapshot completed: $SNAPSHOT_NAME"
        '';
      };
    };
  };
}

{ config, lib, pkgs, ... }:

with lib;

let
  cfg = config.pantherOS.filesystems.btrfs;
in
{
  options.pantherOS.filesystems.btrfs = {
    enable = mkEnableOption "PantherOS Btrfs filesystem configuration";

    enableAutoscrub = mkOption {
      type = types.bool;
      default = true;
      description = "Enable automatic scrubbing of Btrfs volumes";
    };

    autoscrubSettings = {
      interval = mkOption {
        type = types.str;
        default = "weekly";
        description = "How often to scrub Btrfs filesystems";
      };

      fileSystems = mkOption {
        type = types.listOf types.str;
        default = [ "/" ];
        description = "List of Btrfs filesystems to scrub";
      };
    };

    enableAutotrim = mkOption {
      type = types.bool;
      default = true;
      description = "Enable automatic trimming of Btrfs volumes";
    };

    defaultMountOptions = {
      compression = mkOption {
        type = types.str;
        default = "zstd:1";
        description = "Default compression algorithm for Btrfs";
      };

      enable = mkOption {
        type = types.bool;
        default = true;
        description = "Whether to include default mount options";
      };
    };

    extraMountOptions = mkOption {
      type = types.listOf types.str;
      default = [ ];
      description = "Additional mount options for Btrfs";
    };

    enableSnapshots = mkOption {
      type = types.bool;
      default = false;
      description = "Enable Btrfs snapshot management";
    };
  };

  config = mkIf cfg.enable {
    # Btrfs filesystem configuration
    fileSystems = mkIf cfg.defaultMountOptions.enable {
      # Apply default Btrfs mount options to root filesystem
      "/".options = mkIf (cfg.defaultMountOptions.enable && cfg.defaultMountOptions.compression != "") [
        "compress=${cfg.defaultMountOptions.compression}"
        "noatime"
        "ssd"
        "space_cache=v2"
        "discard=async"
      ] ++ cfg.extraMountOptions;
    };

    # Btrfs-specific services
    services.btrfs = {
      autoScrub = mkIf cfg.enableAutoscrub {
        enable = cfg.enableAutoscrub;
        interval = cfg.autoscrubSettings.interval;
        fileSystems = cfg.autoscrubSettings.fileSystems;
      };
    };

    # Additional Btrfs tools for management
    environment.systemPackages = with pkgs; [
      btrfs-progs
    ];

    # Enable automatic TRIM if supported
    services.fstrim = mkIf cfg.enableAutotrim {
      enable = cfg.enableAutotrim;
      interval = "weekly";
    };

    # Additional Btrfs-related kernel modules
    boot = {
      kernelModules = [ "btrfs" ];

      # Enable Btrfs in initrd if needed
      initrd = {
        supportedFilesystems = mkIf cfg.enable [ "btrfs" ];
      };
    };

    # For impermanence and snapshot features, make sure necessary tools are available
    environment.systemPackages = mkIf cfg.enableSnapshots (with pkgs; [
      btrfs-progs
    ]);
  };
}

{ config, lib, ... }:

{
  imports = [
    ./server-impermanence.nix
    ./server-snapshots.nix
  ];

  config = {
    # Universal Btrfs mount options for servers
    fileSystems = {
      "/" = {
        options = [
          "compress=zstd:3"
          "noatime"
          "ssd"
          "space_cache=v2"
          "discard=async"
          "subvol=root"
        ];
      };

      "/nix" = {
        options = [
          "compress=zstd:1"
          "noatime"
          "ssd"
          "space_cache=v2"
          "discard=async"
          "subvol=nix"
        ];
      };

      "/persist" = {
        options = [
          "compress=zstd:3"
          "noatime"
          "ssd"
          "space_cache=v2"
          "discard=async"
          "subvol=persist"
        ];
      };

      "/var/log" = {
        options = [
          "compress=zstd:3"
          "noatime"
          "ssd"
          "space_cache=v2"
          "discard=async"
          "subvol=log"
        ];
      };

      "/var/lib/containers" = {
        options = [
          "compress=zstd:1"
          "noatime"
          "nodatacow"
          "nodatasum"
          "ssd"
          "space_cache=v2"
          "discard=async"
          "subvol=containers"
        ];
      };
    };

    # Enable Btrfs quota support for multi-tenant isolation
    services.btrfs.autoScrub = {
      enable = true;
      interval = "weekly";
      fileSystems = [ "/" ];
    };
  };
}

{ config, lib, pkgs, ... }:

with lib;

let
  cfg = config.pantherOS.security.systemdHardening;
in
{
  options.pantherOS.security.systemdHardening = {
    enable = mkEnableOption "PantherOS systemd service hardening";

    enableSystemdAttackSurfaceReduction = mkOption {
      type = types.bool;
      default = true;
      description = "Reduce systemd attack surface by disabling unused features";
    };

    enableUnifiedCgroupHierarchy = mkOption {
      type = types.bool;
      default = true;
      description = "Enable unified cgroup hierarchy for better isolation";
    };

    enableUserSlices = mkOption {
      type = types.bool;
      default = true;
      description = "Enable user slices for user session isolation";
    };

    defaultServiceMode = mkOption {
      type = types.enum [ "strict" "balanced" "relaxed" ];
      default = "strict";
      description = "Default security mode for services";
    };

    enableServiceSandboxing = mkOption {
      type = types.bool;
      default = true;
      description = "Enable service sandboxing by default";
    };

    globalServiceSettings = {
      enableAmbientCapabilities = mkOption {
        type = types.bool;
        default = false;
        description = "Whether to enable ambient capabilities by default";
      };

      enableSecureBits = mkOption {
        type = types.bool;
        default = true;
        description = "Whether to enable secure bits by default";
      };

      enableNoNewPrivileges = mkOption {
        type = types.bool;
        default = true;
        description = "Whether to enable no-new-privileges by default";
      };

      enablePrivateUsers = mkOption {
        type = types.bool;
        default = false;  # This can break some services
        description = "Whether to enable private users by default";
      };
    };
  };

  config = mkIf cfg.enable {
    # Systemd hardening configurations
    systemd = {
      # Systemd service hardening settings
      enableHardening = true;

      # Reduce attack surface
      service = mkIf cfg.enableSystemdAttackSurfaceReduction {
        # Default secure settings for all services
        default = {
          # Security settings
          NoNewPrivileges = mkDefault cfg.globalServiceSettings.enableNoNewPrivileges;

          # Isolation settings
          PrivateTmp = mkDefault true;
          PrivateDevices = mkDefault true;
          PrivateUsers = mkDefault cfg.globalServiceSettings.enablePrivateUsers;
          ProtectSystem = mkDefault "strict";
          ProtectHome = mkDefault true;
          ProtectHostname = mkDefault true;
          ProtectClock = mkDefault true;
          ProtectKernelTunables = mkDefault true;
          ProtectKernelModules = mkDefault true;
          ProtectControlGroups = mkDefault true;
          RestrictAddressFamilies = mkDefault [ "AF_INET" "AF_INET6" ];
          RestrictRealtime = mkDefault true;
          RestrictSUIDSGID = mkDefault true;
          RemoveIPC = mkDefault true;

          # Capabilities hardening
          AmbientCapabilities = mkIf cfg.globalServiceSettings.enableAmbientCapabilities [ ];
          NoExecPaths = mkDefault [
            "/tmp" "/var/tmp" "/dev/shm"
          ];
          ExecPaths = mkDefault [
            "/nix/store/*" "/run/current-system/sw/bin"
          ];
          ReadOnlyPaths = mkDefault [
            "/etc" "/usr" "/opt"
          ];
          ReadWritePaths = mkDefault [
            "/var" "/tmp" "/run" "/dev"
          ];
        };
      };

      # User session hardening
      user = mkIf cfg.enableUserSlices {
        enable = cfg.enableUserSlices;
        defaultSlice = mkIf (cfg.defaultServiceMode == "strict") "system.slice"; # More restrictive for user services
      };
    };

    # Kernel parameters for systemd hardening
    boot = mkIf cfg.enableUnifiedCgroupHierarchy {
      kernel.sysctl = {
        # Cgroup settings
        "kernel.unprivileged_userns_clone" = mkDefault 0; # Disable unprivileged user namespaces
        "kernel.ctrl-alt-del" = mkDefault 0; # Disable Ctrl+Alt+Del
        "kernel.modules_disabled" = mkDefault 0; # Can be set to 1 in very secure environments (disables module loading after boot)
        "kernel.kexec_load_disabled" = mkDefault 1; # Disable kexec (only when needed)
        "kernel.kptr_restrict" = mkDefault 2; # Restrict kernel pointer leaks
        "kernel.printk" = mkDefault [ 3 3 3 3 ]; # Restrict kernel messages
      };
    };

    # Extra packages for security monitoring
    environment.systemPackages = mkIf cfg.enableSystemdAttackSurfaceReduction [
      pkgs.systemd
      pkgs.acl  # For systemd ACLs
    ];

    # Additional security settings
    security = mkIf cfg.enableSystemdAttackSurfaceReduction {
      # Ensure systemd settings are properly applied
      protectKernelModules = true;
      protectKernelTunables = true;
    };
  };
}

{ config, lib, ... }:

with lib;

let
  cfg = config.pantherOS.security.firewall;
in
{
  options.pantherOS.security.firewall = {
    enable = mkEnableOption "PantherOS advanced firewall configuration";

    enableImplicitNetwork = mkOption {
      type = types.bool;
      default = true;
      description = "Whether to allow traffic on the local loopback and private networks by default";
    };

    allowPing = mkOption {
      type = types.bool;
      default = false;
      description = "Whether to allow incoming ping requests";
    };

    trustTailscale = mkOption {
      type = types.bool;
      default = false;
      description = "Whether to trust Tailscale interfaces completely";
    };

    trustedInterfaces = mkOption {
      type = types.listOf types.str;
      default = [ ];
      description = "List of trusted network interfaces that have more permissions";
    };

    allowedTCPPorts = mkOption {
      type = types.listOf types.port;
      default = [ ];
      description = "List of TCP ports to allow through the firewall";
    };

    allowedUDPPorts = mkOption {
      type = types.listOf types.port;
      default = [ ];
      description = "List of UDP ports to allow through the firewall";
    };

    allowedTCPPortRanges = mkOption {
      type = types.listOf (types.submodule {
        options = {
          from = mkOption { type = types.port; };
          to = mkOption { type = types.port; };
        };
      });
      default = [ ];
      description = "List of TCP port ranges to allow through the firewall";
    };

    allowedUDPPortRanges = mkOption {
      type = types.listOf (types.submodule {
        options = {
          from = mkOption { type = types.port; };
          to = mkOption { type = types.port; };
        };
      });
      default = [ ];
      description = "List of UDP port ranges to allow through the firewall";
    };

    extraForwardRules = mkOption {
      type = types.lines;
      default = "";
      description = "Additional iptables rules for the forward chain";
    };

    logRefusedConnections = mkOption {
      type = types.bool;
      default = false;
      description = "Whether to log refused connections";
    };
  };

  config = mkIf cfg.enable {
    # Advanced firewall configuration
    networking.firewall = {
      enable = true;
      enableIPv6 = true;  # Enable IPv6 firewall as well
      trustedInterfaces =
        cfg.trustedInterfaces
        ++ (mkIf cfg.trustTailscale [ "tailscale0" ]);

      # Apply implicit network settings
      enableImplicitNetwork = cfg.enableImplicitNetwork;

      # Allow ping if configured
      allowPing = cfg.allowPing;

      # Additional ports
      allowedTCPPorts = cfg.allowedTCPPorts;
      allowedUDPPorts = cfg.allowedUDPPorts;

      # Port ranges
      allowedTCPPortRanges = cfg.allowedTCPPortRanges;
      allowedUDPPortRanges = cfg.allowedUDPPortRanges;

      # Log refused connections if configured
      logRefusedConnections = cfg.logRefusedConnections;

      # Additional forward rules
      extraFilterForward = cfg.extraForwardRules;

      # Additional security settings
      synFloodProtector = "systemd";
      stateFile = "/var/lib/nixos/state-firewall";
    };

    # Additional security hardening for the firewall
    boot = {
      kernel.sysctl = {
        # Protect against SYN flood attacks
        "net.ipv4.tcp_syncookies" = mkDefault 1;
        "net.ipv4.tcp_synack_retries" = mkDefault 5;
        "net.ipv4.tcp_max_syn_backlog" = mkDefault 4096;

        # IP spoofing protection
        "net.ipv4.conf.all.rp_filter" = mkDefault 1;
        "net.ipv4.conf.default.rp_filter" = mkDefault 1;

        # Ignore ICMP broadcasts to prevent broadcast attacks
        "net.ipv4.icmp_echo_ignore_broadcasts" = mkDefault 1;

        # Prevent against man in the middle attacks
        "net.ipv4.conf.all.accept_redirects" = mkDefault 0;
        "net.ipv4.conf.all.secure_redirects" = mkDefault 0;
        "net.ipv4.conf.all.send_redirects" = mkDefault 0;

        # Accepting source route can be used for attacks
        "net.ipv4.conf.all.accept_source_route" = mkDefault 0;
        "net.ipv6.conf.all.accept_ra" = mkDefault 0;
        "net.ipv6.conf.all.accept_redirects" = mkDefault 0;
      };
    };
  };
}

# Hetzner Cloud VPS Hardware Specifications

## Overview
- **Host Name**: hetzner-vps
- **Host Type**: Headless Server
- **Primary Purpose**: Primary codespace server, remote development, network services, reverse proxy
- **Use Cases**: Remote development environment, web services, databases, container orchestration, backup target
- **Performance Requirements**: Reliable 24/7 operation with optimal resource efficiency for network services

## Hardware Specifications

### CPU
- **Model**: AMD EPYC 7443P or Intel Xeon Gold 6248R (depending on Hetzner instance type)
- **Cores**: 24 cores / 48 threads (AMD) or 24 cores / 48 threads (Intel)
- **Base Clock**: 2.85 GHz (AMD) / 3.0 GHz (Intel)
- **Boost Clock**: 4.0 GHz (AMD) / 4.0 GHz (Intel)
- **Architecture**: x86_64
- **TDP**: 200W (server-grade)
- **Cache**: 256MB L3 (AMD) / 36MB L3 (Intel)
- **Virtualization**: Full hardware virtualization support (AMD-V/Intel VT-x)
- **Special Features**:
  - Server-grade reliability and error correction
  - Advanced power management for data center efficiency
  - High core count for container orchestration
  - Optimized for sustained high-load operations

### Memory
- **Total RAM**: 96GB DDR4-3200 ECC (Hetzner CPX62 cloud instance)
- **Memory Type**: DDR4-3200 with ECC (Error Correcting Code)
- **Configuration**: 6x16GB DIMM dual-channel configuration
- **Upgradeability**: Up to 128GB supported (Hetzner scaling options)
- **ECC Support**: Yes (server-grade error correction)
- **Memory Bandwidth**: ~200 GB/s
- **Implications**: Ample memory for multiple container workloads, database caching, and development environments

### Storage
- **Primary Storage**: 800GB NVMe PCIe 3.0 SSD (Hetzner local disk)
- **Storage Performance**: ~3000 MB/s read, ~2000 MB/s write
- **IOPS Performance**: ~100K random read IOPS, ~80K random write IOPS
- **Storage Interface**: NVMe over PCIe 3.0
- **Data Persistence**: Local SSD with Hetzner backup options
- **Expansion**:
  - Hetzner Object Storage (S3-compatible) integration
  - Additional volume mounts via Hetzner Volume API
  - Network-attached storage via Tailscale
- **Backup Strategy**: Automated snapshots and external backup replication

### Network
- **Primary Interface**: 1 Gbps Ethernet (Hetzner network)
- **Public IPv4**: Static IP address (Hetzner-assigned)
- **Public IPv6**: Dual-stack support (IPv4/IPv6)
- **Private Network**: Hetzner vSwitch for inter-instance communication
- **Bandwidth**: 1 Gbps dedicated, no traffic limits (fair use policy)
- **Special Features**:
  - DDoS protection (Hetzner network-level)
  - Low-latency European backbone
  - Direct peering with major cloud providers
  - Tailscale exit node capability

### Virtualization
- **Hypervisor**: KVM (Kernel-based Virtual Machine)
- **Virtualization Type**: Full virtualization with hardware acceleration
- **Container Support**: Native container support (Docker/Podman)
- **Nested Virtualization**: Supported for development/testing scenarios
- **Resource Isolation**: cgroups v2 with systemd slice isolation

